import React, { useState } from 'react';
import './App.css';

function Weather() {
  const [shows, setShow] = useState({
    location: '',
    tempertaure: '',
    description: '',
  });

  const [id, setId] = useState('');

  const search = (e) => {
    if (e.key === 'Enter') {
      let key = '886705b4c1182eb1c69f28eb8c520e20';
      fetch(
        'https://api.openweathermap.org/data/2.5/weather?q=' +
          { id } +
          '&units=metric&APPID=' +
          { key }
      )
        .then((response) => response.json())
        .then((data) => {
          handleState(data);
          setId = '';
        });
    }
  };

  const handleState = (d) => {
    let celcius = Math.round(parseFloat(d.main.temp) - 273.15);
    setShow({
      ...shows,
      description: d.weather[0].main,
      tempertaure: celcius,
      location: d.name,
    });
  };

  return (
    <div>
      <form>
        <input
          type='text'
          placeholder='enter the country Name'
          onChange={(e) => setId(e.target.value)}
          value={id}
          onKeyPress={search}
        />
      </form>
      <h1>Hello Tanveer</h1>
      <hr />
      <div className='location-box'>
        <div className='location'>location : {shows.location}</div>
      </div>
      <div className='weather-box'>
        <div className='temp'>Temperature : {shows.tempertaure} &deg;</div>
        <div className='weather'>Weather :{shows.description}</div>
      </div>
    </div>
  );
}

export default Weather;
