import React, { useReducer } from 'react';

function Parent() {
  const initialState = {
    count: 0,
  };
  const reducer = (curr_state, action) => {
    switch (action.type) {
      case 'increment':
        return { curr_state: curr_state.count + 1 };
      default:
        return state;
    }
  };
  const [state, dispatch] = useReducer(reducer, initialState);

  return (
    <div>
      count : {initialState.count}
      {dispatch({ type: 'increment' })}
    </div>
  );
}

export default Parent;
