import React, { useState } from 'react';
import './App.css';

function Weather() {
  const [shows, setShow] = useState({
    location: '',
    tempertaure: '',
  });

  const data = (cityId) => {
    let key = '886705b4c1182eb1c69f28eb8c520e20';
    fetch(
      'https://api.openweathermap.org/data/2.5/weather?id=' +
        cityId +
        '&appid=' +
        key
    )
      .then((response) => response.json())
      .then((data) => {
        setShow({ ...shows, location: data.name });
      });
  };

  return (
    <div>
      {data(6167865)}
      {shows.location}
    </div>
  );
}

export default Weather;
