import React, { useState } from 'react';
import './App.css';

function Weather() {
  const [shows, setShow] = useState({
    location: '',
    tempertaure: '',
    description: '',
  });

  const [id, setId] = useState('');

  const data = (cityId) => {
    let key = '886705b4c1182eb1c69f28eb8c520e20';
    fetch(
      'https://api.openweathermap.org/data/2.5/weather?id=' +
        cityId +
        '&appid=' +
        key
    )
      .then((response) => response.json())
      .then((data) => handleState(data));
  };

  const handleState = (d) => {
    let celcius = Math.round(parseFloat(d.main.temp) - 273.15);
    setShow({
      ...shows,
      description: d.weather[0].main,
      tempertaure: celcius,
      location: d.name,
    });
  };

  return (
    <div>
      <form>
        <input
          type='text'
          placeholder='enter the country id'
          value={id}
          onChange={(e) => setId(e.target.value)}
        />
      </form>
      <h1>Hello Tanveer</h1>
      <hr />
      {data({ id })}
      <div>{shows.location}</div>
      <div>{shows.tempertaure} &deg;</div>
      <div>{shows.description}</div>
    </div>
  );
}

export default Weather;
