import React, { useState } from 'react';
import './App.css';

function Weather() {
  const [shows, setShow] = useState({
    location: '',
    tempertaure: '',
    description: '',
  });

  const [id, setId] = useState('');

  const data = ({ id }) => {
    console.log('data function called');
    let key = '886705b4c1182eb1c69f28eb8c520e20';
    fetch(
      'https://api.openweathermap.org/data/2.5/weather?id=' +
        id +
        '&appid=' +
        key
    )
      .then((response) => response.json())
      .then((data) => handleState(data));
  };

  const handleState = (d) => {
    let celcius = Math.round(parseFloat(d.main.temp) - 273.15);
    setShow({
      ...shows,
      description: d.weather[0].main,
      tempertaure: celcius,
      location: d.name,
    });
  };

  const handlSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <div>
      <form onSubmit={(e) => handleSubmit}>
        <input
          type='text'
          placeholder='enter the country id'
          value={id}
          onChange={(e) => setId(e.target.value)}
        />
        <button type='submit' onClick={data}>
          submit
        </button>
      </form>
      <h1>Hello Tanveer</h1>
      <hr />

      <div>location : {shows.location}</div>
      <div>Temperature : {shows.tempertaure} &deg;</div>
      <div>Weather : {shows.description}</div>
    </div>
  );
}

export default Weather;
