import React from 'react';
import './App.css';

const changeBackground = (temp) => {};

function App() {
  const [shows, setShow] = useState({
    location: '',
    tempertaure: '',
    description: '',
  });

  const data = (cityId) => {
    let key = '886705b4c1182eb1c69f28eb8c520e20';
    fetch(
      'https://api.openweathermap.org/data/2.5/weather?id=' +
        cityId +
        '&appid=' +
        key
    )
      .then((response) => response.json())
      .then((data) => handleState(data));
  };

  const handleState = (d) => {
    let celcius = Math.round(parseFloat(d.main.temp) - 273.15);
    setShow({
      ...shows,
      description: d.weather[0].main,
      tempertaure: celcius,
      location: d.name,
    });
  };

  return (
    <div>
      {data(6167865)}
      <div>{shows.location}</div>
      <div>{shows.tempertaure} &deg;</div>
      <div>{shows.description}</div>
    </div>
  );
}

export default App;
